import React, { useEffect, useState } from 'react'


const Countries = () => {

    const [countries, setCountries] = useState([])

    const fetchCountryData = async () => {
        const response = await fetch('http://api.countrylayer.com/v2/all?access_key=7f8eb63542089e7b9d96c1aef764db43')
        const data = await response.json()
        setCountries(data)
        console.log(data)
    }

    useEffect(() => {
        fetchCountryData()
    }, [])

    return (
     <>
     <section>
            {countries.map(({name, capital, region})=>(
                <article key={name}>
                    <h2>Nome: {name}</h2>
                    <ul>
                        <li>Capital : {capital}</li>
                        <li>Região : {region}</li>
                    </ul>
                </article>
            ))}


     </section>
     </>

       
    )
}

export default Countries
