import React, { useEffect, useState } from 'react'


const Countries = () => {

    const [countries, setCountries] = useState([])

    const fetchCountryData = async () => {
        const response = await fetch('http://api.countrylayer.com/v2/all?access_key=21f09f4cb8805ed99812a3c7d2629963')
        const data = await response.json()
        setCountries(data)
        console.log(data)
    }

    useEffect(() => {
        fetchCountryData()
    }, [])

    return (
        <>
            {countries.map(({ name, capital, region }) => (
                <article s  key={name}>
                    <h2>Nome: {name}</h2>
                    <ul>
                        <li>Capital : {capital}</li>
                        <li>Região : {region}</li>
                    </ul>
                </article>
            ))}
        </>
    )
}

export default Countries
